#!/bin/bashecho "Getting started"# Bundle docs into zero-dependency HTML filenpx redoc-cli bundle openapi.json && \mv redoc-static.html index.html && \echo "Changed name from redoc-static.html to index.html" && \# Inject custom CSSsed -i '6 i \ \ <link rel="stylesheet" type="text/css" href="styles.css"/>' index.html && \# Add faviconsed -i '7 i \ \ <link rel="icon" type="image/png" href="images/favicon.png"/>' index.html && \echo -e "\nDone!"